{
  "framework": "nextjs",
  "version": 2,
  "buildCommand": "pnpm run build",
  "outputDirectory": "apps/web/out",
  "installCommand": "pnpm install --frozen-lockfile",
  "devCommand": "pnpm run dev",
  "env": {
    "NODE_VERSION": "25",
    "NPM_VERSION": "10",
    "PNPM_VERSION": "9.15.0",
    "TURBO_VERSION": "2.0.0",
    "PLATFORM": "vercel"
  },
  "build": {
    "env": {
      "NODE_VERSION": "25",
      "PLATFORM": "vercel"
    }
  },
  "functions": {
    "app/api/**/*.js": {
      "runtime": "nodejs25.x"
    },
    "api/**/*.js": {
      "runtime": "nodejs25.x"
    }
  },
  "headers": [
    {
      "source": "/api/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "s-maxage=86400, stale-while-revalidate"
        }
      ]
    },
    {
      "source": "/data/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "public, max-age=31536000, immutable"
        }
      ]
    }
  ],
  "rewrites": [
    {
      "source": "/api/cathedral/(.*)",
      "destination": "/api/cathedral/$1"
    }
  ],
  "domains": [
    "cathedral-avalon-labs.vercel.app",
    "avalon-labs-cathedral.vercel.app",
    "cathedral-rosslyn-explorer.vercel.app"
  ],
  "regions": ["iad1", "sfo1"],
  "installCommand": "curl -fsSL https://get.pnpm.io/install.sh | sh - && export PATH=\"$HOME/.local/share/pnpm:$PATH\" && pnpm install --frozen-lockfile"
}
